import React, { useState } from 'react';
import axios from 'axios';
import { FaCloudUploadAlt } from 'react-icons/fa';
import './App.css';

const SHEET_ID = '1Bhzvt8jltG9iA8YiD-msyEMt-vcPtnODTHc7GKWjQ-M'; // Replace with your Google Sheets ID

function App() {
  const [file, setFile] = useState(null);

  const handleDragOver = (event) => {
    event.preventDefault();
  };

  const handleDrop = (event) => {
    event.preventDefault();
    const droppedFile = event.dataTransfer.files[0];
    setFile(droppedFile);
  };

  const handleUpload = async () => {
    try {
      const formData = new FormData();
      formData.append('resume', file);

      await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      alert('Resume parsed and data stored successfully.');
    } catch (error) {
      console.error(error);
      alert('Error parsing resume. Please try again.');
    }
  };

  return (
    <div className="App">
      <h1 className="title">Resume Parser</h1>
      <div
        className="dropzone"
        onDragOver={handleDragOver}
        onDrop={handleDrop}
      >
        {file ? (
          <p>File selected: {file.name}</p>
        ) : (
          <>
            <FaCloudUploadAlt className="upload-icon" />
            <p>Drag and drop your resume file here, or click to select a file.</p>
          </>
        )}
      </div>
      <button onClick={handleUpload} disabled={!file} className="upload-button">
        Upload
      </button>
    </div>
  );
}

export default App;
