import React, { useState } from 'react';
import axios from 'axios';
import { GoogleLogin } from 'react-google-login';
import Dropzone from 'react-dropzone';
import './App.css';
const CLIENT_ID = 'YOUR_CLIENT_ID'; // Replace with your Google OAuth client ID
const SHEET_ID = 'YOUR_SHEET_ID'; // Replace with your Google Sheets ID

function App() {
  const [file, setFile] = useState(null);

  const handleDrop = (acceptedFiles) => {
    setFile(acceptedFiles[0]);
  };

  const handleUpload = async () => {
    try {
      const formData = new FormData();
      formData.append('resume', file);

      await axios.post('http://localhost:5000/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      alert('Resume parsed and data stored successfully.');
    } catch (error) {
      console.error(error);
      alert('Error parsing resume. Please try again.');
    }
  };

  const handleGoogleLogin = async (response) => {
    try {
      const idToken = response.tokenId;
      const formData = new FormData();
      formData.append('id_token', idToken);

      await axios.post('http://localhost:5000/authenticate', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });

      alert('Successfully authenticated with Google.');
    } catch (error) {
      console.error(error);
      alert('Google authentication failed. Please try again.');
    }
  };

  return (
    <div className="App">
      <h1>Resume Parser</h1>
      <Dropzone onDrop={handleDrop}>
        {({ getRootProps, getInputProps }) => (
          <div {...getRootProps()} className="dropzone">
            <input {...getInputProps()} />
            {file ? (
              <p>File selected: {file.name}</p>
            ) : (
              <p>Drag and drop your resume file here, or click to select a file.</p>
            )}
          </div>
        )}
      </Dropzone>
      <button onClick={handleUpload} disabled={!file}>Upload</button>
      <GoogleLogin
        clientId={CLIENT_ID}
        buttonText="Login with Google"
        onSuccess={handleGoogleLogin}
        onFailure={handleGoogleLogin}
        cookiePolicy={'single_host_origin'}
      />
    </div>
  );
}

export default App;
